mapper.select:
select s.*,u.realname from b_stat s,s_user u where s.phonenumber=u.phonenumber and s.isvalid>0

    and s.phonenumber=#{phonenumber}


    and s.interviewtime >= #{starttime}


    and s.interviewtime <= #{endtime}

order by s.ctime desc



        LIMIT #{key_offset},8


        LIMIT 0,8

select CONCAT(u.realname,' - ',s.companyname) as title,s.interviewtime as start,s.id as statid from b_stat
s,s_user u where s.phonenumber=u.phonenumber and s.isvalid>0

    and s.phonenumber=#{phonenumber}
